HRLocales = {}
---@param txt string
---@param add any
function HRLocale(txt, add)local conf = Config.HRLocaleC;if(HRLocales[conf])then if(HRLocales[conf][txt])then if(add)then return string.format(HRLocales[conf][txt], add):gsub("^%l", string.upper)elseif(HRLocales['en'] and HRLocales['en'][txt] and conf ~= 'en' and add)then return string.format(HRLocales['en'][txt], add):gsub("^%l", string.upper)else return HRLocales[conf][txt]end else return ('%s/%s does not exist as translation (Source script: %s)'):format(conf, txt, GetInvokingResource())end elseif(conf~='en'and HRLocales['en']and HRLocales['en'][txt])then return string.format(HRLocales['en'][txt], add):gsub("^%l", string.upper)else return ('[%s] does not exist as language translation in %s resource'):format(conf, GetInvokingResource())end end;HRL = HRLocale